    private void spriteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_spriteActionPerformed
        // mengubah tombol sprite menjadi 1 pilihan saja:
        if(sprite.isSelected()){
           cola.setSelected(false);
           pepsi.setSelected(false);
           coffe.setSelected(false);
           water.setSelected(false);
           rootbeer.setSelected(false);
        }
    }//GEN-LAST:event_spriteActionPerformed

