    private void pilihActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pilihActionPerformed
        //untuk membayar:
        double money = 0;
        money = Double.parseDouble(uang.getText());
        
        if(sprite.isSelected()){
            money = money - 8000;
        }else if(cola.isSelected()){
            money = money - 7000;    
            }else if(pepsi.isSelected()){
            money = money - 6000;
            }else if(coffe.isSelected()){
            money = money - 10000;    
            }else if(water.isSelected()){
            money = money - 3000;
            }else if(rootbeer.isSelected()){
            money = money - 9000;    
            }
        
        if (money < 0){
            JOptionPane.showMessageDialog(rootPane, "Maaf Uang Anda Tidak Cukup");
        }else{
            kembalian.setText(Double.toString(money));
        }
    }//GEN-LAST:event_pilihActionPerformed

